% Method to analyze a calStruct generated by a @Calibrator object.
function obj = analyze(obj)
 
    numFiles = length(obj.calStructOBJarray);

    for i = 1:numFiles

        DescribeMonCal(obj.calStructOBJarray{i});
    
        % Print some more information
        if (obj.calStructOBJarray{i}.inputCalHasNewStyleFormat)
            fprintf('  * Graphics Driver  : %s - based\n', obj.newStyleCalarray{i}.describe.graphicsEngine);
            fprintf('  * Screen size      : %d x %d pixels\n', obj.newStyleCalarray{i}.describe.screenSizePixel(1), obj.newStyleCalarray{i}.describe.screenSizePixel(2));
            fprintf('  * Target size      : %d x %d pixels\n', obj.newStyleCalarray{i}.describe.boxSize(1), obj.newStyleCalarray{i}.describe.boxSize(1));
            fprintf('  * Target position  : (%d,%d) pixels (offset from center)\n', obj.newStyleCalarray{i}.describe.boxOffsetX, obj.newStyleCalarray{i}.describe.boxOffsetY);
            fprintf('  * Background Color : %2.3f %2.3f %2.3f\n', obj.newStyleCalarray{i}.describe.bgColor(1), obj.newStyleCalarray{i}.describe.bgColor(2), obj.newStyleCalarray{i}.describe.bgColor(3));
            fprintf('  * Foreground Color : %2.3f %2.3f %2.3f\n', obj.newStyleCalarray{i}.describe.fgColor(1), obj.newStyleCalarray{i}.describe.fgColor(2), obj.newStyleCalarray{i}.describe.fgColor(3));
            fprintf('  * Averages         : %d\n', obj.newStyleCalarray{i}.describe.nAverage);  
            fprintf('  * Radiometer Model : %s\n', obj.newStyleCalarray{i}.describe.meterModel);
            fprintf('\n');
        end
    end

    obj.refitData(); 

    obj.plotAllData();

end